package com.ocp.casesolution.adapterimport android.content.Intentimport android.util.Logimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.ocp.casesolution.Rimport com.ocp.casesolution.model.LocationSearchLatLongimport com.ocp.casesolution.view.DetailsActivityimport com.ocp.casesolution.view.MapsActivityimport kotlinx.android.synthetic.main.list_city.view.*class LocationSearchLatLongAdapter(val locationSearchList: ArrayList<LocationSearchLatLong>):RecyclerView.Adapter<LocationSearchLatLongAdapter.LocationSearchViewHolder>() {    class LocationSearchViewHolder(var view : View) : RecyclerView.ViewHolder(view.rootView) {    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): LocationSearchViewHolder {        val inflater= LayoutInflater.from(parent.context)        val view = inflater.inflate(R.layout.list_city,parent,false)        return LocationSearchViewHolder(view)    }    override fun getItemCount(): Int {        return locationSearchList.size    }    override fun onBindViewHolder(holder: LocationSearchViewHolder, position: Int) {        holder.view.cityName.text=locationSearchList[position].cityTitle        val woeid = locationSearchList[position].woeid        holder.view.setOnClickListener {            val intent = Intent(it.context,DetailsActivity::class.java)            intent.putExtra("woeid",woeid)            it.context.startActivity(intent)        }    }    fun updateLocationList(newLocationList: List<LocationSearchLatLong>) {        locationSearchList.clear()        locationSearchList.addAll(newLocationList)        notifyDataSetChanged()    }}